public class QuotientAndRemainder {

	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
		int number1 = 50, number2 = 25; 
		int quotient = number1 / number2; int remainder = number1 % number2;
		System.out.println("Quotient is: " + quotient); System.out.println("Remainder is: " + remainder);
	}

}
